import React, { Component } from 'react'
import ReactDOM from 'react-dom'

class Weddings extends Component {

  constructor(props) {
    super(props);

    this.state = {
      term: '',
      autoCompleteResults: []
    };


    $.getJSON('/search_users?q=' + this.state.term)
      .then(response => this.setState({ autoCompleteResults: response }))
  }

  getAutoCompleteResults(e){
    this.setState({
      term: e.target.value
    }, () => {
      $.getJSON('/search_users?q=' + this.state.term)
        .then(response => this.setState({ autoCompleteResults: response }))
    });
  }

  toggleSelectedUser(e) {
    const elem = e.target;
    const userId = elem.getAttribute('data-id'); 

    $.ajax({
      url: `toggle_user?user_id=${userId}`,
      dataType: 'json',
      method: 'PUT'
    }).done(function(data) {
      console.log(data)
      if (data.remaining_users >= 0 && data.toggled === true) {
        $(elem).closest('.user-item').toggleClass('selected')
      } else {
        alert("You've already selected the maximum number of people.")
      }
    });
  }

  render() {
    let autoCompleteList = this.state.autoCompleteResults.map((response, index) => {
      return (
        <div key={index} data-id={response.id} className="user-item" onClick={ this.toggleSelectedUser.bind(this) }>
          <img src={response.avatar} className="user-picture" width="150px" height="150px" data-id={response.id} />
          <h2 className="user-name" data-id={response.id}>{response.name}</h2>
        </div>
      )
    });

    return (
      <div>
        <input ref={ (input) => { this.searchBar = input } } value={ this.state.term } onChange={ this.getAutoCompleteResults.bind(this) } type='text' placeholder='Search...' />
        <div className="user-item-wrapper">
          { autoCompleteList }
        </div>
      </div>
    )
  }
}

document.addEventListener('DOMContentLoaded', () => {
  ReactDOM.render(
    <Weddings />,
    document.body.appendChild(document.createElement('div')),
  )
});


